import { useLocation } from "wouter";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Star, Clock, MessageCircle, Edit2 } from "lucide-react";
import type { Bid, User, MakerProfile } from "@shared/schema";

interface BidCardProps {
  bid: Bid & { 
    maker?: User & { makerProfile?: MakerProfile | null }
  };
  onAccept?: (bidId: string) => void;
  onReject?: (bidId: string) => void;
  onContact?: (makerId: string, projectId: string) => void;
  onConfirmDelivery?: (bidId: string) => void;
  onEdit?: (bidId: string) => void;
  onRebid?: () => void;
  isClient?: boolean;
  isMyBid?: boolean;
  currentUserId?: string;
  currentUserName?: string;
  isPending?: boolean;
  isProjectDeleted?: boolean;
}

export function BidCard({ bid, onAccept, onReject, onContact, onConfirmDelivery, onEdit, onRebid, isClient, isMyBid, currentUserId, currentUserName, isPending, isProjectDeleted }: BidCardProps) {
  const [, setLocation] = useLocation();
  const profile = bid.maker?.makerProfile;
  const initials = (bid.maker?.username || bid.maker?.email)?.[0].toUpperCase() || "M";

  const canEditBid = isMyBid && bid.status === "pending" && currentUserId === bid.makerId && !isProjectDeleted;

  const handleCardClick = () => {
    if (isClient && onContact) {
      onContact(bid.makerId, bid.projectId);
    }
  };

  const handleViewProfile = (e: React.MouseEvent) => {
    e.stopPropagation();
    const profileId = isMyBid && currentUserId ? currentUserId : bid.maker?.id;
    if (profileId) {
      setLocation(`/user/${profileId}`);
    }
  };

  return (
    <Card 
      className={`border ${!isProjectDeleted ? 'hover:border-primary/50 transition-colors hover-elevate active-elevate-2 cursor-pointer' : ''}`}
      onClick={handleCardClick}
      data-testid={`card-bid-${bid.id}`}
    >
      <div className="p-6">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Maker Info */}
          <div className="flex items-start gap-4 flex-1">
            <Avatar className="h-14 w-14 flex-shrink-0">
              <AvatarImage src={bid.maker?.profileImageUrl || undefined} alt={initials} />
              <AvatarFallback>{initials}</AvatarFallback>
            </Avatar>
            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-2">
                <button
                  onClick={handleViewProfile}
                  className="font-semibold text-lg hover:text-primary hover-elevate cursor-pointer transition-colors"
                  data-testid={`text-maker-name-${bid.id}`}
                >
                  {isMyBid && currentUserName ? currentUserName : bid.maker?.username || bid.maker?.email || bid.maker?.id || "Desconocido"}
                </button>
                {profile && (
                  <Badge variant="secondary" className="text-xs">
                    {profile.printerType}
                  </Badge>
                )}
              </div>
              {profile && (
                <div className="flex items-center gap-4 text-sm text-muted-foreground mb-3">
                  {profile.rating && parseFloat(profile.rating) > 0 && (
                    <span className="flex items-center gap-1">
                      <Star className="h-4 w-4 fill-yellow-400 text-yellow-400" />
                      {parseFloat(profile.rating).toFixed(1)}
                      <span className="text-xs">({profile.totalReviews})</span>
                    </span>
                  )}
                  {profile.location && (
                    <span>{profile.location}</span>
                  )}
                </div>
              )}
              {bid.message && (
                <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`text-bid-message-${bid.id}`}>
                  {bid.message}
                </p>
              )}
              {profile?.capabilities && (
                <p className="text-xs text-muted-foreground mt-2 line-clamp-2">
                  {profile.capabilities}
                </p>
              )}
            </div>
          </div>

          {/* Bid Details */}
          <div className="flex flex-col items-end justify-between gap-4 flex-shrink-0">
            <div className="text-right">
              <p className="text-3xl font-bold text-primary" data-testid={`text-bid-price-${bid.id}`}>
                â‚¬{parseFloat(bid.price).toFixed(2)}
              </p>
              <p className="flex items-center justify-end gap-1.5 text-sm text-muted-foreground mt-1" data-testid={`text-bid-delivery-${bid.id}`}>
                <Clock className="h-4 w-4" />
                {bid.deliveryDays} dÃ­as
              </p>
            </div>

            {/* Actions */}
            <div className="flex flex-col gap-2 items-end">
              {/* Pending bid status badge - but show as rejected if project deleted */}
              {(bid.status === "pending" || bid.status === undefined || bid.status === null) && !isProjectDeleted && (
                <Badge className="bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-100" data-testid={`badge-pending-${bid.id}`}>
                  Pendiente
                </Badge>
              )}

              {/* If project deleted, pending bids are treated as rejected */}
              {(bid.status === "pending" || bid.status === undefined || bid.status === null) && isProjectDeleted && (
                <Badge variant="secondary" data-testid={`badge-rejected-deleted-${bid.id}`}>
                  Rechazada
                </Badge>
              )}

              {/* Chat button - Available for clients only if project not deleted */}
              {isClient && !isProjectDeleted && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onContact?.(bid.makerId, bid.projectId)}
                  disabled={isPending}
                  data-testid={`button-contact-${bid.id}`}
                >
                  <MessageCircle className="h-4 w-4 mr-1" />
                  Chat
                </Button>
              )}

              {/* Pending bid actions */}
              {isClient && (bid.status === "pending" || bid.status === undefined || bid.status === null) && (
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => onReject?.(bid.id)}
                    disabled={isPending}
                    data-testid={`button-reject-${bid.id}`}
                  >
                    Rechazar
                  </Button>
                  <Button
                    size="sm"
                    onClick={() => onAccept?.(bid.id)}
                    disabled={isPending}
                    data-testid={`button-accept-${bid.id}`}
                  >
                    Aceptar
                  </Button>
                </div>
              )}

              {/* Maker edit button */}
              {canEditBid && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onEdit?.(bid.id)}
                  disabled={isPending}
                  data-testid={`button-edit-bid-${bid.id}`}
                >
                  <Edit2 className="h-4 w-4 mr-1" />
                  Editar
                </Button>
              )}

              {/* Accepted bid status and actions */}
              {bid.status === "accepted" && (
                <>
                  <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100">
                    Aceptada
                  </Badge>
                  {isClient && !bid.deliveryConfirmedAt && (
                    <Button
                      size="sm"
                      variant="default"
                      onClick={() => onConfirmDelivery?.(bid.id)}
                      disabled={isPending}
                      data-testid={`button-confirm-delivery-${bid.id}`}
                    >
                      Confirmar RecepciÃ³n
                    </Button>
                  )}
                  {bid.deliveryConfirmedAt && (
                    <Badge className="bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-100">
                      Entrega Confirmada
                    </Badge>
                  )}
                </>
              )}

              {/* Rejected bid status */}
              {bid.status === "rejected" && (
                <>
                  <Badge variant="secondary">
                    Rechazada
                  </Badge>
                  {isMyBid && onRebid && (
                    <Button
                      size="sm"
                      onClick={onRebid}
                      data-testid={`button-rebid-${bid.id}`}
                    >
                      Volver a Ofertar
                    </Button>
                  )}
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </Card>
  );
}

client/src/components/TokenInputForm.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

interface TokenInputFormProps {
  onTokenSubmit: (token: string) => void;
  onCancel: () => void;
}

export default function TokenInputForm({ onTokenSubmit, onCancel }: TokenInputFormProps) {
  const [token, setToken] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (token.trim()) {
      onTokenSubmit(token.trim());
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800 rounded-md p-4 text-sm">
        <p className="text-blue-900 dark:text-blue-100">
          <strong>Instrucciones:</strong> Copia el token del email de recuperaciÃ³n que recibiste y pÃ©galo abajo.
        </p>
      </div>

      <div>
        <Label htmlFor="reset-token" className="text-sm">
          Token de RecuperaciÃ³n
        </Label>
        <Input
          id="reset-token"
          type="text"
          placeholder="Pega el token aquÃ­"
          value={token}
          onChange={(e) => setToken(e.target.value)}
          data-testid="input-reset-token"
          required
        />
        <p className="text-xs text-muted-foreground mt-2">
          El token tiene aproximadamente 20-25 caracteres
        </p>
      </div>

      <Button
        type="submit"
        className="w-full"
        disabled={!token.trim()}
        size="lg"
        data-testid="button-submit-token"
      >
        Continuar
      </Button>

      <Button 
        type="button"
        variant="ghost" 
        className="w-full"
        onClick={onCancel}
        data-testid="button-cancel-token"
      >
        Cancelar
      </Button>
    </form>
  );
}

client/src/components/MakerProfileDialog.content:
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertMakerProfileSchema, type InsertMakerProfile, type MakerProfile } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { useAuth } from "@/hooks/useAuth";

interface MakerProfileDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  profile?: MakerProfile | null;
}

export function MakerProfileDialog({ open, onOpenChange, profile }: MakerProfileDialogProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Get registration data from sessionStorage if available
  const getRegistrationData = () => {
    try {
      const data = sessionStorage.getItem("makerRegistrationData");
      return data ? JSON.parse(data) : null;
    } catch {
      return null;
    }
  };

  const registrationData = getRegistrationData();

  const form = useForm<InsertMakerProfile>({
    resolver: zodResolver(insertMakerProfileSchema),
    defaultValues: profile ? {
      userId: profile.userId,
      printerType: profile.printerType,
      materials: profile.materials || [],
      maxPrintDimensionX: profile.maxPrintDimensionX || undefined,
      maxPrintDimensionY: profile.maxPrintDimensionY || undefined,
      maxPrintDimensionZ: profile.maxPrintDimensionZ || undefined,
      hasMulticolor: profile.hasMulticolor || false,
      maxColors: profile.maxColors || 1,
      location: profile.location || "",
      capabilities: profile.capabilities || "",
    } : registrationData ? {
      userId: user?.id || "",
      printerType: registrationData.printerType || "Ender3",
      materials: [],
      maxPrintDimensionX: registrationData.maxPrintDimensionX,
      maxPrintDimensionY: registrationData.maxPrintDimensionY,
      maxPrintDimensionZ: registrationData.maxPrintDimensionZ,
      hasMulticolor: registrationData.hasMulticolor || false,
      maxColors: registrationData.maxColors || 1,
      location: registrationData.addressPostalCode ? `${registrationData.addressPostalCode}` : "",
      capabilities: "",
    } : {
      userId: user?.id || "",
      printerType: "Ender3",
      materials: [],
      hasMulticolor: false,
      maxColors: 1,
      location: "",
      capabilities: "",
    },
  });

  const mutation = useMutation({
    mutationFn: async (data: InsertMakerProfile) => {
      if (profile) {
        await apiRequest("PUT", "/api/maker-profile", data);
      } else {
        await apiRequest("POST", "/api/maker-profile", data);
      }
    },
    onSuccess: () => {
      toast({
        title: profile ? "Perfil actualizado" : "Perfil creado",
        description: "Tu perfil de maker ha sido guardado exitosamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/maker-profile"] });
      queryClient.invalidateQueries({ queryKey: ["/api/projects/available"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      onOpenChange(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "No autorizado",
          description: "Iniciando sesiÃ³n...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message || "No se pudo guardar el perfil",
        variant: "destructive",
      });
    },
  });

  // Reset form when profile changes (for existing profiles)
  useEffect(() => {
    if (profile) {
      form.reset({
        userId: profile.userId,
        printerType: profile.printerType,
        materials: profile.materials || [],
        maxPrintDimensionX: profile.maxPrintDimensionX || undefined,
        maxPrintDimensionY: profile.maxPrintDimensionY || undefined,
        maxPrintDimensionZ: profile.maxPrintDimensionZ || undefined,
        hasMulticolor: profile.hasMulticolor || false,
        maxColors: profile.maxColors || 1,
        location: profile.location || "",
        capabilities: profile.capabilities || "",
      });
    }
  }, [profile, form]);

  const onSubmit = (data: InsertMakerProfile) => {
    mutation.mutate({ ...data, userId: user?.id || "" });
  };

  const availableMaterials = ["PLA", "ABS", "PETG", "TPU", "Nylon", "Resina", "PC", "ASA"];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>{profile ? "Editar" : "Configurar"} Perfil de Maker</DialogTitle>
          <DialogDescription>
            Completa la informaciÃ³n de tu perfil para que los clientes conozcan tus capacidades
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Printer Type */}
            <FormField
              control={form.control}
              name="printerType"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Tipo de Impresora</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-printer-type">
                        <SelectValue placeholder="Selecciona el tipo" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="Ender3">Ender 3</SelectItem>
                      <SelectItem value="BambooLab">Bamboo Lab</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Materials */}
            <FormField
              control={form.control}
              name="materials"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Materiales Disponibles</FormLabel>
                  <div className="grid grid-cols-2 gap-4">
                    {availableMaterials.map((material) => (
                      <div key={material} className="flex items-center space-x-2">
                        <Checkbox
                          id={`material-${material}`}
                          checked={field.value?.includes(material)}
                          onCheckedChange={(checked) => {
                            const current = field.value || [];
                            if (checked) {
                              field.onChange([...current, material]);
                            } else {
                              field.onChange(current.filter((m) => m !== material));
                            }
                          }}
                          data-testid={`checkbox-material-${material}`}
                        />
                        <label
                          htmlFor={`material-${material}`}
                          className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer"
                        >
                          {material}
                        </label>
                      </div>
                    ))}
                  </div>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Print Dimensions */}
            <div className="space-y-4">
              <FormLabel>Dimensiones MÃ¡ximas de ImpresiÃ³n (mm)</FormLabel>
              <div className="grid grid-cols-3 gap-4">
                <FormField
                  control={form.control}
                  name="maxPrintDimensionX"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>X (ancho)</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          placeholder="200"
                          {...field}
                          value={field.value ?? ""}
                          onChange={(e) => field.onChange(e.target.value ? parseInt(e.target.value) : undefined)}
                          data-testid="input-dimension-x"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="maxPrintDimensionY"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Y (profundidad)</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          placeholder="200"
                          {...field}
                          value={field.value ?? ""}
                          onChange={(e) => field.onChange(e.target.value ? parseInt(e.target.value) : undefined)}
                          data-testid="input-dimension-y"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="maxPrintDimensionZ"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Z (altura)</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          placeholder="200"
                          {...field}
                          value={field.value ?? ""}
                          onChange={(e) => field.onChange(e.target.value ? parseInt(e.target.value) : undefined)}
                          data-testid="input-dimension-z"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </div>

            {/* Multicolor */}
            <FormField
              control={form.control}
              name="hasMulticolor"
              render={({ field }) => (
                <FormItem className="flex flex-row items-start space-x-3 space-y-0 rounded-md border p-4">
                  <FormControl>
                    <Checkbox
                      checked={field.value}
                      onCheckedChange={field.onChange}
                      data-testid="checkbox-multicolor"
                    />
                  </FormControl>
                  <div className="space-y-1 leading-none">
                    <FormLabel>ImpresiÃ³n Multicolor</FormLabel>
                    <FormDescription>
                      Â¿Tu impresora puede imprimir en mÃºltiples colores?
                    </FormDescription>
                  </div>
                </FormItem>
              )}
            />

            {/* Max Colors - Only show if multicolor is enabled */}
            {form.watch("hasMulticolor") && (
              <FormField
                control={form.control}
                name="maxColors"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>NÃºmero MÃ¡ximo de Colores</FormLabel>
                    <FormControl>
                      <Input 
                        type="number"
                        min="2"
                        placeholder="Ej: 4" 
                        {...field}
                        value={field.value || ""}
                        onChange={(e) => field.onChange(e.target.value ? parseInt(e.target.value) : undefined)}
                        data-testid="input-max-colors"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            )}

            {/* Location */}
            <FormField
              control={form.control}
              name="location"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>UbicaciÃ³n</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Ej: Madrid, EspaÃ±a" 
                      {...field} 
                      value={field.value || ""}
                      data-testid="input-location"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Capabilities */}
            <FormField
              control={form.control}
              name="capabilities"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Capacidades y Experiencia</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe tu experiencia, tipos de proyectos en los que te especializas, acabados que ofreces, etc."
                      className="min-h-24"
                      {...field}
                      value={field.value || ""}
                      data-testid="input-capabilities"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                className="flex-1"
                disabled={!profile}
                data-testid="button-back-profile"
              >
                â† AtrÃ¡s
              </Button>
              <Button
                type="submit"
                disabled={mutation.isPending}
                className="flex-1"
                data-testid="button-save-profile"
              >
                {mutation.isPending ? "Guardando..." : (profile ? "Actualizar Perfil" : "Crear Perfil")}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/client-login.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Package, Mail } from "lucide-react";

interface ClientLoginPageProps {
  onSuccess?: () => void;
}

export default function ClientLoginPage({ onSuccess }: ClientLoginPageProps) {
  const [email, setEmail] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const { toast } = useToast();

  const mutation = useMutation({
    mutationFn: async (emailAddr: string) => {
      await apiRequest("POST", "/api/auth/client-login", { email: emailAddr });
    },
    onSuccess: () => {
      toast({
        title: "Â¡Verifica tu email!",
        description: "Enviamos un link de acceso a tu email. Haz clic para continuar.",
      });
      setSubmitted(true);
      setTimeout(() => onSuccess?.(), 2000);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "No se pudo procesar el login",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
      mutation.mutate(email);
    } else {
      toast({
        title: "Email invÃ¡lido",
        description: "Por favor ingresa un email vÃ¡lido",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary via-secondary to-primary flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <Card className="shadow-2xl border-0">
          <CardHeader className="space-y-4 text-center">
            <div className="bg-primary/10 w-16 h-16 rounded-full flex items-center justify-center mx-auto">
              <Package className="h-8 w-8 text-primary" />
            </div>
            <div>
              <CardTitle className="text-3xl">Cliente</CardTitle>
              <CardDescription className="text-base">
                Ingresa tu email para acceder
              </CardDescription>
            </div>
          </CardHeader>
          <CardContent>
            {submitted ? (
              <div className="text-center py-8">
                <div className="bg-green-100 dark:bg-green-900/30 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Mail className="h-8 w-8 text-green-600 dark:text-green-400" />
                </div>
                <h3 className="font-semibold mb-2">Â¡Link enviado!</h3>
                <p className="text-sm text-muted-foreground">
                  Revisa tu email para confirmar tu acceso
                </p>
              </div>
            ) : (
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">
                    DirecciÃ³n de Email
                  </label>
                  <Input
                    type="email"
                    placeholder="tu@email.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    disabled={mutation.isPending}
                    data-testid="input-client-email"
                    required
                  />
                </div>
                <Button
                  type="submit"
                  className="w-full"
                  disabled={mutation.isPending || !email}
                  size="lg"
                  data-testid="button-client-login"
                >
                  {mutation.isPending ? "Enviando..." : "Continuar"}
                </Button>
                <p className="text-xs text-center text-muted-foreground">
                  RecibirÃ¡s un link seguro en tu email para acceder
                </p>
              </form>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/ClientRegisterForm.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import ClientLocationMapPicker from "@/components/ClientLocationMapPicker";
import { Mail, Lock, User, MapPin } from "lucide-react";

interface ClientRegisterFormProps {
  onSuccess?: () => void;
  onBack?: () => void;
}

export default function ClientRegisterForm({ onSuccess, onBack }: ClientRegisterFormProps) {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    // Location (optional)
    hasLocation: false,
    addressPostalCode: "",
    addressLatitude: "40.4168",
    addressLongitude: "-3.7038",
    addressRadius: 0,
    // Policies
    acceptedTerms: false,
    acceptedPrivacy: false,
  });
  const { toast } = useToast();

  const mutation = useMutation({
    mutationFn: async () => {
      if (form.password !== form.confirmPassword) {
        throw new Error("Las contraseÃ±as no coinciden");
      }
      if (form.password.length < 8) {
        throw new Error("La contraseÃ±a debe tener al menos 8 caracteres");
      }
      if (!form.acceptedTerms || !form.acceptedPrivacy) {
        throw new Error("Debes aceptar las polÃ­ticas y privacidad");
      }

      const res = await apiRequest("POST", "/api/auth/register", {
        firstName: form.firstName,
        lastName: form.lastName,
        username: form.username,
        email: form.email,
        password: form.password,
        userType: "client",
        hasLocation: form.hasLocation,
        addressPostalCode: form.hasLocation ? form.addressPostalCode : null,
        addressLatitude: form.hasLocation ? form.addressLatitude : null,
        addressLongitude: form.hasLocation ? form.addressLongitude : null,
        addressRadius: form.hasLocation ? form.addressRadius : null,
        acceptedTermsAt: new Date().toISOString(),
        acceptedPrivacyAt: new Date().toISOString(),
      });
      return res;
    },
    onSuccess: (response: any) => {
      toast({
        title: "Â¡Registro exitoso!",
        description: "Tu cuenta ha sido creada. Revisa los logs del servidor para el link de verificaciÃ³n.",
      });
      if (response.verificationToken) {
        console.log("=== VERIFICATION TOKEN ===");
        console.log("Token:", response.verificationToken);
        console.log("Link: /verify?token=" + response.verificationToken);
        console.log("==========================");
      }
      setTimeout(() => onSuccess?.(), 3000);
    },
    onError: (error: any) => {
      toast({
        title: "Error en registro",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validate all fields
    if (!form.firstName || !form.lastName || !form.username) {
      toast({
        title: "Error",
        description: "Por favor completa nombre, apellido y usuario",
        variant: "destructive",
      });
      return;
    }

    if (!form.email || !form.password || !form.confirmPassword) {
      toast({
        title: "Error",
        description: "Por favor completa email y contraseÃ±a",
        variant: "destructive",
      });
      return;
    }

    if (form.password.length < 8) {
      toast({
        title: "Error",
        description: "La contraseÃ±a debe tener al menos 8 caracteres",
        variant: "destructive",
      });
      return;
    }

    if (form.password !== form.confirmPassword) {
      toast({
        title: "Error",
        description: "Las contraseÃ±as no coinciden",
        variant: "destructive",
      });
      return;
    }

    if (form.hasLocation && !form.addressPostalCode) {
      toast({
        title: "Error",
        description: "Por favor ingresa el cÃ³digo postal",
        variant: "destructive",
      });
      return;
    }

    if (!form.acceptedTerms || !form.acceptedPrivacy) {
      toast({
        title: "Error",
        description: "Debes aceptar las polÃ­ticas y privacidad",
        variant: "destructive",
      });
      return;
    }

    mutation.mutate();
  };

  const passwordsMatch = form.password === form.confirmPassword;
  const passwordError = form.password && form.confirmPassword && !passwordsMatch;

  return (
    <form onSubmit={handleSubmit} className="space-y-4 max-h-[80vh] overflow-y-auto pr-2">
      <h3 className="text-sm font-semibold text-primary sticky top-0 bg-background py-2">Crear Cuenta - Cliente</h3>

      {/* INFORMACIÃ“N BÃSICA */}
      <div className="space-y-3 p-3 bg-muted/30 rounded-md border">
        <Label className="text-sm font-semibold">InformaciÃ³n BÃ¡sica</Label>

        <div className="grid grid-cols-2 gap-2">
          <Input
            name="firstName"
            placeholder="Nombre"
            value={form.firstName}
            onChange={handleChange}
            disabled={mutation.isPending}
            required
            data-testid="input-client-firstname"
          />
          <Input
            name="lastName"
            placeholder="Apellido"
            value={form.lastName}
            onChange={handleChange}
            disabled={mutation.isPending}
            required
            data-testid="input-client-lastname"
          />
        </div>

        <Input
          name="username"
          placeholder="Usuario"
          value={form.username}
          onChange={handleChange}
          disabled={mutation.isPending}
          required
          data-testid="input-client-username"
        />
      </div>

      {/* CREDENCIALES */}
      <div className="space-y-3 p-3 bg-muted/30 rounded-md border">
        <Label className="text-sm font-semibold">Credenciales</Label>

        <div>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              name="email"
              type="email"
              placeholder="tu@email.com"
              value={form.email}
              onChange={handleChange}
              disabled={mutation.isPending}
              required
              className="pl-10"
              data-testid="input-client-email"
            />
          </div>
        </div>

        <div>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              name="password"
              type="password"
              placeholder="ContraseÃ±a (mÃ­n. 8 caracteres)"
              value={form.password}
              onChange={handleChange}
              disabled={mutation.isPending}
              required
              className="pl-10"
              data-testid="input-client-password"
            />
          </div>
        </div>

        <div>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              name="confirmPassword"
              type="password"
              placeholder="Confirmar contraseÃ±a"
              value={form.confirmPassword}
              onChange={handleChange}
              disabled={mutation.isPending}
              required
              className={`pl-10 ${passwordError ? 'border-red-500' : ''}`}
              data-testid="input-client-confirm-password"
            />
          </div>
          {passwordError && (
            <p className="text-red-500 text-sm mt-1" data-testid="error-password-mismatch">
              Las contraseÃ±as no coinciden
            </p>
          )}
        </div>
      </div>

      {/* UBICACIÃ“N (OPCIONAL) */}
      <div className="space-y-3 p-3 bg-muted/50 rounded-md border">
        <div className="flex items-center space-x-2">
          <Checkbox
            id="has-location"
            name="hasLocation"
            checked={form.hasLocation}
            onCheckedChange={(checked) =>
              setForm({ ...form, hasLocation: checked as boolean })
            }
            data-testid="checkbox-client-has-location"
          />
          <Label htmlFor="has-location" className="text-sm cursor-pointer font-medium">
            Compartir mi ubicaciÃ³n (Opcional)
          </Label>
        </div>

        {form.hasLocation && (
          <div className="space-y-3 pt-3 border-t">
            <Input
              name="addressPostalCode"
              placeholder="CÃ³digo Postal (ej: 28001)"
              value={form.addressPostalCode}
              onChange={handleChange}
              disabled={mutation.isPending}
              required
              data-testid="input-client-postal-code"
            />

            <ClientLocationMapPicker
              value={{
                latitude: form.addressLatitude,
                longitude: form.addressLongitude,
                radius: form.addressRadius,
              }}
              onChange={(data) =>
                setForm({
                  ...form,
                  addressLatitude: data.latitude,
                  addressLongitude: data.longitude,
                  addressRadius: data.radius,
                })
              }
            />
          </div>
        )}
      </div>

      {/* POLÃTICAS Y PRIVACIDAD */}
      <div className="space-y-3 p-3 bg-muted/30 rounded-md border">
        <Label className="text-sm font-semibold block">Aceptar PolÃ­ticas</Label>

        <div className="flex items-start space-x-2">
          <Checkbox
            id="accept-terms"
            name="acceptedTerms"
            checked={form.acceptedTerms}
            onCheckedChange={(checked) =>
              setForm({ ...form, acceptedTerms: checked as boolean })
            }
            data-testid="checkbox-accept-terms"
            className="mt-1"
          />
          <Label htmlFor="accept-terms" className="text-sm cursor-pointer leading-relaxed">
            Acepto los{" "}
            <a href="/terms" className="text-primary hover:underline">
              TÃ©rminos de Servicio
            </a>
          </Label>
        </div>

        <div className="flex items-start space-x-2">
          <Checkbox
            id="accept-privacy"
            name="acceptedPrivacy"
            checked={form.acceptedPrivacy}
            onCheckedChange={(checked) =>
              setForm({ ...form, acceptedPrivacy: checked as boolean })
            }
            data-testid="checkbox-accept-privacy"
            className="mt-1"
          />
          <Label htmlFor="accept-privacy" className="text-sm cursor-pointer leading-relaxed">
            Acepto la{" "}
            <a href="/privacy" className="text-primary hover:underline">
              PolÃ­tica de Privacidad
            </a>
          </Label>
        </div>
      </div>

      {/* BOTONES */}
      <div className="flex gap-2 sticky bottom-0 bg-background py-2 border-t">
        <Button
          type="button"
          variant="outline"
          className="flex-1"
          onClick={onBack}
          disabled={mutation.isPending}
          data-testid="button-back-client-register"
        >
          â† AtrÃ¡s
        </Button>
        <Button
          type="submit"
          className="flex-1"
          disabled={
            mutation.isPending ||
            passwordError ||
            !form.acceptedTerms ||
            !form.acceptedPrivacy ||
            (form.hasLocation && !form.addressPostalCode)
          }
          data-testid="button-client-register-submit"
        >
          {mutation.isPending ? "Registrando..." : "Registrarse"}
        </Button>
      </div>
    </form>
  );
}

