  const { data: acceptedBid } = useQuery<(Bid & { maker?: User & { makerProfile?: MakerProfile | null } }) | null>({
    queryKey: ["/api/projects", projectId, "accepted-bid"],
    enabled: !!projectId && !!user && isClient,
  });

