import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertProjectSchema, type InsertProject } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Upload, CheckCircle } from "lucide-react";

interface UploadProjectDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function UploadProjectDialog({ open, onOpenChange }: UploadProjectDialogProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [fileName, setFileName] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);

  const form = useForm<InsertProject>({
    resolver: zodResolver(insertProjectSchema),
    defaultValues: {
      name: "",
      stlFileName: "",
      stlFileContent: undefined,
      description: "",
      material: "",
      specifications: {
        dimensionX: "",
        dimensionY: "",
        dimensionZ: "",
      },
    },
  });

  const mutation = useMutation({
    mutationFn: async (data: InsertProject) => {
      console.log("[UploadProjectDialog] Submitting project:", data);
      const response = await apiRequest("POST", "/api/projects", data);
      console.log("[UploadProjectDialog] Server response:", response);
      return response;
    },
    onSuccess: () => {
      console.log("[UploadProjectDialog] Upload successful");
      setIsSuccess(true);

      toast({
        title: "Â¡Proyecto Publicado!",
        description: "Tu proyecto estÃ¡ disponible para que los makers hagan ofertas",
      });

      // Reset form and close dialog after 2 seconds
      setTimeout(() => {
        queryClient.invalidateQueries({ queryKey: ["/api/projects/my-projects"] });
        queryClient.invalidateQueries({ queryKey: ["/api/projects/stats"] });
        queryClient.invalidateQueries({ queryKey: ["/api/projects/available"] });
        form.reset();
        setFileName("");
        setIsSuccess(false);
        onOpenChange(false);
      }, 2000);
    },
    onError: (error: Error) => {
      console.error("[UploadProjectDialog] Upload error:", error);
      if (isUnauthorizedError(error)) {
        toast({
          title: "No autorizado",
          description: "Iniciando sesiÃ³n...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error al publicar",
        description: error.message || "No se pudo crear el proyecto",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertProject) => {
    console.log("[UploadProjectDialog] Form validation passed, submitting...");
    console.log("[UploadProjectDialog] Form data:", data);
    mutation.mutate(data);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setFileName(file.name);
      form.setValue("stlFileName", file.name);

      // Read file as base64
      const reader = new FileReader();
      reader.onload = (event) => {
        const content = event.target?.result as string;
        // Extract base64 from data URL
        const base64Content = content.split(',')[1] || content;
        form.setValue("stlFileContent", base64Content);
      };
      reader.readAsDataURL(file);
    }
  };

  const isSubmitDisabled = mutation.isPending || !fileName || !form.formState.isValid;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        {isSuccess ? (
          <div className="flex flex-col items-center justify-center py-12 text-center">
            <div className="bg-green-100 dark:bg-green-900/30 w-16 h-16 rounded-full flex items-center justify-center mb-4 mx-auto">
              <CheckCircle className="h-8 w-8 text-green-600 dark:text-green-400" />
            </div>
            <h3 className="text-2xl font-bold mb-2">Â¡Publicado!</h3>
            <p className="text-muted-foreground max-w-md">
              Tu proyecto estÃ¡ disponible y los makers pueden empezar a hacer ofertas
            </p>
          </div>
        ) : (
          <>
            <DialogHeader>
              <DialogTitle>Subir Nuevo Proyecto STL</DialogTitle>
              <DialogDescription>
                Completa los detalles de tu proyecto para que los makers puedan enviar ofertas precisas
              </DialogDescription>
            </DialogHeader>

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                {/* File Upload */}
                <FormItem>
                  <FormLabel>Archivo STL</FormLabel>
                  <FormControl>
                    <div className="border-2 border-dashed rounded-lg p-6 text-center hover:border-primary transition-colors">
                      <input
                        type="file"
                        accept=".stl"
                        onChange={handleFileChange}
                        className="hidden"
                        id="stl-file-input"
                        data-testid="input-stl-file"
                      />
                      <label
                        htmlFor="stl-file-input"
                        className="cursor-pointer flex flex-col items-center gap-2"
                      >
                        <Upload className="h-10 w-10 text-muted-foreground" />
                        <span className="text-sm text-muted-foreground">
                          {fileName || "Haz clic para seleccionar un archivo STL"}
                        </span>
                      </label>
                    </div>
                  </FormControl>
                  <FormDescription>
                    Formatos aceptados: .stl (mÃ¡ximo 50MB)
                  </FormDescription>
                </FormItem>

                {/* Project Name */}
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Nombre del Proyecto</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="Ej: Prototipo de engranaje mecÃ¡nico" 
                          {...field} 
                          data-testid="input-project-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Description */}
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>DescripciÃ³n</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Describe los detalles de tu proyecto, tolerancias, acabado requerido, etc."
                          className="min-h-24"
                          {...field}
                          data-testid="input-project-description"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Material */}
                <FormField
                  control={form.control}
                  name="material"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Material Preferido</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-material">
                            <SelectValue placeholder="Selecciona el material" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="PLA">PLA</SelectItem>
                          <SelectItem value="ABS">ABS</SelectItem>
                          <SelectItem value="PETG">PETG</SelectItem>
                          <SelectItem value="TPU">TPU</SelectItem>
                          <SelectItem value="Nylon">Nylon</SelectItem>
                          <SelectItem value="Resina">Resina</SelectItem>
                          <SelectItem value="Otro">Otro</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Dimensions */}
                <div className="space-y-3">
                  <FormLabel>Dimensiones (mm)</FormLabel>
                  <p className="text-sm text-muted-foreground">Especifica las medidas mÃ¡ximas del objeto</p>
                  <div className="grid grid-cols-3 gap-3">
                    <div>
                      <Input
                        type="number"
                        placeholder="Largo (X)"
                        value={(form.watch("specifications") as any)?.dimensionX || ""}
                        onChange={(e) =>
                          form.setValue("specifications", {
                            ...((form.getValues("specifications") || {}) as any),
                            dimensionX: e.target.value,
                          })
                        }
                        data-testid="input-dimension-x"
                      />
                      <p className="text-xs text-muted-foreground mt-1">Eje X</p>
                    </div>
                    <div>
                      <Input
                        type="number"
                        placeholder="Ancho (Y)"
                        value={(form.watch("specifications") as any)?.dimensionY || ""}
                        onChange={(e) =>
                          form.setValue("specifications", {
                            ...((form.getValues("specifications") || {}) as any),
                            dimensionY: e.target.value,
                          })
                        }
                        data-testid="input-dimension-y"
                      />
                      <p className="text-xs text-muted-foreground mt-1">Eje Y</p>
                    </div>
                    <div>
                      <Input
                        type="number"
                        placeholder="Alto (Z)"
                        value={(form.watch("specifications") as any)?.dimensionZ || ""}
                        onChange={(e) =>
                          form.setValue("specifications", {
                            ...((form.getValues("specifications") || {}) as any),
                            dimensionZ: e.target.value,
                          })
                        }
                        data-testid="input-dimension-z"
                      />
                      <p className="text-xs text-muted-foreground mt-1">Eje Z</p>
                    </div>
                  </div>
                </div>

                <div className="flex gap-3 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => onOpenChange(false)}
                    className="flex-1"
                    data-testid="button-back-upload"
                  >
                    â† AtrÃ¡s
                  </Button>
                  <Button
                    type="submit"
                    disabled={isSubmitDisabled}
                    className="flex-1"
                    data-testid="button-submit-project"
                  >
                    {mutation.isPending ? "Publicando..." : "Publicar Proyecto"}
                  </Button>
                </div>

                {form.formState.errors && Object.keys(form.formState.errors).length > 0 && (
                  <div className="bg-red-50 dark:bg-red-900/20 p-3 rounded-md">
                    <p className="text-sm text-red-700 dark:text-red-400 font-semibold">Errores de validaciÃ³n:</p>
                    <ul className="text-sm text-red-600 dark:text-red-300 mt-1 space-y-1">
                      {Object.entries(form.formState.errors).map(([field, error]: any) => (
                        <li key={field}>â€¢ {field}: {error?.message || "Error desconocido"}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </form>
            </Form>
          </>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ChatInterface.tsx.content:
import { useEffect, useRef, useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Send, CheckCheck } from "lucide-react";
import { format } from "date-fns";
import { es } from "date-fns/locale";
import type { Message, User } from "@shared/schema";

interface ChatInterfaceProps {
  otherUserId: string;
  otherUser: User | undefined;
  currentUserId: string;
  projectId?: string;
  marketplaceDesignId?: string;
}

export function ChatInterface({ otherUserId, otherUser, currentUserId, projectId, marketplaceDesignId }: ChatInterfaceProps) {
  const [messageText, setMessageText] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const markReadTimeoutRef = useRef<NodeJS.Timeout>();

  const { data: messages = [] } = useQuery<Message[]>({
    queryKey: ["/api/messages", projectId, marketplaceDesignId, otherUserId],
    queryFn: async () => {
      try {
        let url = `/api/messages?otherUserId=${encodeURIComponent(otherUserId)}`;
        if (projectId) {
          url += `&projectId=${encodeURIComponent(projectId)}`;
        } else if (marketplaceDesignId) {
          url += `&marketplaceDesignId=${encodeURIComponent(marketplaceDesignId)}`;
        }
        const response = await fetch(url, {
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
        });
        if (!response.ok) throw new Error(`HTTP ${response.status}`);
        return response.json();
      } catch (error) {
        console.error("Failed to fetch messages:", error);
        return [];
      }
    },
    enabled: !!otherUserId,
    refetchInterval: 2000,
  });

  const sendMutation = useMutation({
    mutationFn: async (content: string) => {
      const messageData: any = {
        receiverId: otherUserId,
        content,
      };

      if (projectId) {
        messageData.projectId = projectId;
        messageData.contextType = "project";
      } else if (marketplaceDesignId) {
        messageData.marketplaceDesignId = marketplaceDesignId;
        messageData.contextType = "marketplace_design";
      }

      await apiRequest("POST", "/api/messages", messageData);
    },
    onSuccess: () => {
      setMessageText("");
      queryClient.invalidateQueries({ queryKey: ["/api/messages", projectId, marketplaceDesignId, otherUserId] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "No se pudo enviar el mensaje",
        variant: "destructive",
      });
    },
  });

  // Mark messages as read when chat is opened
  useEffect(() => {
    if (otherUserId) {
      // Debounce the mark as read call to avoid too many requests
      if (markReadTimeoutRef.current) {
        clearTimeout(markReadTimeoutRef.current);
      }

      markReadTimeoutRef.current = setTimeout(async () => {
        try {
          let url = `/api/messages/mark-read/${otherUserId}`;
          if (projectId) {
            url += `?projectId=${encodeURIComponent(projectId)}`;
          } else if (marketplaceDesignId) {
            url += `?marketplaceDesignId=${encodeURIComponent(marketplaceDesignId)}`;
          }
          await apiRequest("PUT", url, {});
          // Invalidate conversations to remove unread badges
          queryClient.invalidateQueries({ queryKey: ["/api/my-conversations-full"] });
          queryClient.invalidateQueries({ queryKey: ["/api/my-conversations"] });
        } catch (error) {
          console.error("Failed to mark messages as read:", error);
        }
      }, 300); // Small delay to avoid multiple calls
    }

    return () => {
      if (markReadTimeoutRef.current) {
        clearTimeout(markReadTimeoutRef.current);
      }
    };
  }, [otherUserId, projectId, marketplaceDesignId, queryClient]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = () => {
    if (messageText.trim()) {
      sendMutation.mutate(messageText);
    }
  };

  const otherUserInitials = (otherUser?.username || otherUser?.email)?.[0].toUpperCase() || "U";

  return (
    <Card className="flex flex-col h-[500px] border">
      {/* Header */}
      <div className="border-b p-4 flex items-center gap-3">
        <Avatar>
          <AvatarImage src={otherUser?.profileImageUrl || undefined} />
          <AvatarFallback>{otherUserInitials}</AvatarFallback>
        </Avatar>
        <div>
          <p className="font-semibold">
            {otherUser?.username || otherUser?.email}
          </p>
          <p className="text-xs text-muted-foreground">
            {otherUser?.userType === "maker" ? "Maker" : "Cliente"}
          </p>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.length === 0 ? (
          <div className="text-center text-muted-foreground py-8">
            <p>No hay mensajes aÃºn</p>
            <p className="text-sm">Â¡Inicia la conversaciÃ³n!</p>
          </div>
        ) : (
          messages.map((msg) => (
            <div
              key={msg.id}
              className={`flex ${msg.senderId === currentUserId ? "justify-end" : "justify-start"}`}
              data-testid={`message-bubble-${msg.id}`}
            >
              <div
                className={`max-w-xs px-4 py-2 rounded-lg ${
                  msg.senderId === currentUserId
                    ? "bg-blue-200 dark:bg-blue-800 text-blue-950 dark:text-blue-100"
                    : "bg-muted text-muted-foreground"
                }`}
              >
                <p className="text-sm">{msg.content}</p>
                <div className="flex items-center justify-between gap-2 mt-1">
                  <p className="text-xs opacity-70">
                    {format(new Date(msg.createdAt), "HH:mm", { locale: es })}
                  </p>
                  {msg.senderId === currentUserId && (
                    <CheckCheck 
                      className="h-4 w-4 text-blue-950/40 dark:text-blue-100/40"
                      data-testid={`ticks-${msg.id}`}
                    />
                  )}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="border-t p-4 flex gap-2">
        <Input
          placeholder="Escribe un mensaje..."
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
          disabled={sendMutation.isPending}
          data-testid="input-message"
        />
        <Button
          size="icon"
          onClick={handleSend}
          disabled={sendMutation.isPending || !messageText.trim()}
          data-testid="button-send-message"
        >
          <Send className="h-4 w-4" />
        </Button>
      </div>
    </Card>
  );
}

