  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { HalfStarRating } from "./HalfStarRating";

interface RatingDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  title: string;
  description: string;
  targetName: string;
  onSubmit: (rating: number, comment?: string) => void;
  isLoading?: boolean;
}

export function RatingDialog({
  open,
  onOpenChange,
  title,
  description,
  targetName,
  onSubmit,
  isLoading,
}: RatingDialogProps) {
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [comment, setComment] = useState("");

  const handleSubmit = () => {
    if (rating === 0) {
      return;
    }
    onSubmit(rating, comment);
    setRating(0);
    setComment("");
  };

  const handleOpenChange = (newOpen: boolean) => {
    if (!newOpen && !isLoading) {
      onOpenChange(newOpen);
    }
  };

  const handleStarClick = (star: number, event: React.MouseEvent<HTMLButtonElement>) => {
    const button = event.currentTarget;
    const rect = button.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const isLeftHalf = x < rect.width / 2;

    if (isLeftHalf) {
      setRating(star - 0.5);
    } else {
      setRating(star);
    }
  };

  const handleStarHover = (star: number, event: React.MouseEvent<HTMLButtonElement>) => {
    const button = event.currentTarget;
    const rect = button.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const isLeftHalf = x < rect.width / 2;

    if (isLeftHalf) {
      setHoverRating(star - 0.5);
    } else {
      setHoverRating(star);
    }
  };

  return (
    <Dialog open={open} onOpenChange={handleOpenChange}>
      <DialogContent data-testid="dialog-rating">
        <DialogHeader>
          <DialogTitle>{title}</DialogTitle>
          <DialogDescription>{description}</DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          <div>
            <label className="block text-sm font-medium mb-3">
              Mi calificaciÃ³n para {targetName}
            </label>
            <HalfStarRating
              rating={rating}
              hoverRating={hoverRating}
              onStarClick={handleStarClick}
              onStarHover={handleStarHover}
              onMouseLeave={() => setHoverRating(0)}
              isLoading={isLoading}
            />
            {rating > 0 && (
              <p className="text-sm text-muted-foreground mt-2">
                {rating} / 5 estrellas
              </p>
            )}
          </div>

          <div>
            <label htmlFor="comment" className="block text-sm font-medium mb-2">
              Comentario (opcional)
            </label>
            <Textarea
              id="comment"
              placeholder="Comparte tu experiencia con este maker..."
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              disabled={isLoading}
              className="resize-none"
              data-testid="input-comment"
            />
          </div>
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={isLoading}
            data-testid="button-cancel"
          >
            Cancelar
          </Button>
          <Button
            onClick={handleSubmit}
            disabled={rating === 0 || isLoading}
            data-testid="button-submit-rating"
          >
            {isLoading ? "Enviando..." : "Enviar CalificaciÃ³n"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

