import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { EmptyState } from "@/components/EmptyState";
import { ArrowLeft, Zap, CheckCircle, XCircle } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { es } from "date-fns/locale";
import type { Project } from "@shared/schema";
import { Badge } from "@/components/ui/badge";

export default function MakerBidsList() {
  const [, setLocation] = useLocation();
  const { user, isLoading: authLoading } = useAuth();
  const { toast } = useToast();

  const { data: myBids, isLoading: bidsLoading } = useQuery<(any & { project?: Project })[]>({
    queryKey: ["/api/bids/my-bids"],
    enabled: !!user,
  });

  if (!authLoading && !user) {
    toast({
      title: "No autorizado",
      description: "Iniciando sesiÃ³n...",
      variant: "destructive",
    });
    setTimeout(() => {
      window.location.href = "/api/login";
    }, 500);
    return null;
  }

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando...</p>
        </div>
      </div>
    );
  }

  const activeBids = myBids?.filter(b => b.status === "pending" && !b.project?.deletedAt) || [];
  const acceptedBids = myBids?.filter(b => b.status === "accepted") || [];
  // Include explicitly rejected AND pending bids from deleted projects (treated as rejected)
  const rejectedBids = myBids?.filter(b => b.status === "rejected" || (b.status === "pending" && b.project?.deletedAt)) || [];
  const inactiveBids = [...(acceptedBids || []), ...(rejectedBids || [])];

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 bg-background/95 backdrop-blur-md border-b">
        <div className="container mx-auto px-4 py-4 max-w-7xl">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => window.history.back()}
            className="flex items-center gap-2"
            data-testid="button-back"
          >
            <ArrowLeft className="h-4 w-4" />
            Volver
          </Button>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8 max-w-7xl">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Mis Ofertas Activas</h1>
          <p className="text-muted-foreground">
            {activeBids.length} oferta{activeBids.length !== 1 ? "s" : ""}
          </p>
        </div>

        {bidsLoading ? (
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="pt-6 pb-6 h-24" />
              </Card>
            ))}
          </div>
        ) : activeBids.length > 0 ? (
          <div className="space-y-4">
            {activeBids.map((bid) => (
              <Card 
                key={bid.id}
                className="hover-elevate cursor-pointer"
                onClick={() => setLocation(`/project/${bid.projectId}`)}
              >
                <CardContent className="pt-6 pb-6">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      <h3 className="font-bold text-lg">{bid.project?.name}</h3>
                      <p className="text-muted-foreground text-sm mt-1">{bid.project?.description}</p>
                      <p className="text-xs text-muted-foreground mt-2">
                        Publicado {formatDistanceToNow(new Date(bid.project?.createdAt || new Date()), { locale: es, addSuffix: true })}
                      </p>
                    </div>
                    <div className="flex items-center gap-2 bg-primary/10 px-3 py-2 rounded-lg">
                      <Zap className="h-5 w-5 text-primary" />
                      <span className="font-bold text-primary">Activa</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <EmptyState
            icon={Zap}
            title="No has hecho ninguna oferta"
            description="No tienes ofertas pendientes en este momento"
          />
        )}

        {/* Inactive Bids History */}
        {inactiveBids.length > 0 && (
          <div className="mt-12">
            <h2 className="text-2xl font-bold mb-6">HistÃ³rico de Ofertas</h2>
            <div className="space-y-4">
              {acceptedBids?.map((bid) => (
                <Card 
                  key={`accepted-${bid.id}`}
                  className="hover-elevate cursor-pointer opacity-75"
                  onClick={() => setLocation(`/project/${bid.projectId}`)}
                >
                  <CardContent className="pt-6 pb-6">
                    <div className="flex items-start justify-between gap-4">
                      <div className="flex-1">
                        <h3 className="font-bold text-lg">{bid.project?.name}</h3>
                        <p className="text-muted-foreground text-sm mt-1">{bid.project?.description}</p>
                        <p className="text-xs text-muted-foreground mt-2">
                          Publicado {formatDistanceToNow(new Date(bid.project?.createdAt || new Date()), { locale: es, addSuffix: true })}
                        </p>
                      </div>
                      <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100 gap-1">
                        <CheckCircle className="h-4 w-4" />
                        Aceptada
                      </Badge>
                    </div>
                  </CardContent>
                </Card>
              ))}

              {rejectedBids?.map((bid) => (
                <Card 
                  key={`rejected-${bid.id}`}
                  className="hover-elevate cursor-pointer opacity-75"
                  onClick={() => setLocation(`/project/${bid.projectId}`)}
                >
                  <CardContent className="pt-6 pb-6">
                    <div className="flex items-start justify-between gap-4">
                      <div className="flex-1">
                        <h3 className="font-bold text-lg">{bid.project?.name}</h3>
                        <p className="text-muted-foreground text-sm mt-1">{bid.project?.description}</p>
                        <p className="text-xs text-muted-foreground mt-2">
                          Publicado {formatDistanceToNow(new Date(bid.project?.createdAt || new Date()), { locale: es, addSuffix: true })}
                        </p>
                      </div>
                      <Badge variant="secondary" className="gap-1">
                        <XCircle className="h-4 w-4" />
                        Rechazada
                      </Badge>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

client/src/pages/maker-project-details.t.content:
import { useEffect, useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { StatusBadge } from "@/components/StatusBadge";
import { ChatDialog } from "@/components/ChatDialog";
import { BidEditDialog } from "@/components/BidEditDialog";
import { BidSubmissionDialog } from "@/components/BidSubmissionDialog";
import { ArrowLeft, Calendar, FileText, Package, Download, MessageCircle, Edit2 } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { es } from "date-fns/locale";
import type { Project, User } from "@shared/schema";

export default function MakerProjectDetails() {
  const [match, params] = useRoute("/maker/project/:id");
  const [, setLocation] = useLocation();
  const { user, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [chatDialogOpen, setChatDialogOpen] = useState(false);
  const [editBidDialogOpen, setEditBidDialogOpen] = useState(false);
  const [rebidDialogOpen, setRebidDialogOpen] = useState(false);
  const [projectOwner, setProjectOwner] = useState<User | null>(null);

  const projectId = params?.id;

  const { data: project, isLoading: projectLoading } = useQuery<Project>({
    queryKey: ["/api/projects", projectId],
    enabled: !!projectId && !!user,
  });

  const { data: myBid } = useQuery<any>({
    queryKey: ["/api/projects", projectId, "my-bid"],
    enabled: !!projectId && !!user,
  });

  // Fetch project owner
  useEffect(() => {
    if (project?.userId) {
      const fetchOwner = async () => {
        try {
          const response = await fetch(`/api/user/${project.userId}`, {
            credentials: "include",
          });
          if (response.ok) {
            const owner = await response.json();
            setProjectOwner(owner);
          }
        } catch (error) {
          console.error("Failed to fetch project owner:", error);
        }
      };
      fetchOwner();
    }
  }, [project?.userId]);

  useEffect(() => {
    if (!authLoading && !user) {
      toast({
        title: "No autorizado",
        description: "Iniciando sesiÃ³n...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [user, authLoading, toast]);

  if (!match || authLoading || projectLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando proyecto...</p>
        </div>
      </div>
    );
  }

  if (!user || !project) {
    return null;
  }

  const handleDownloadSTL = async () => {
    try {
      const response = await fetch(`/api/projects/${projectId}/download-stl`, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error("Failed to download STL");
      }
      const data = await response.json();

      toast({
        title: "STL Listo",
        description: `Archivo: ${data.fileName}`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "No se pudo descargar el STL",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-card border-b sticky top-0 z-10">
        <div className="container mx-auto px-4 py-4 max-w-7xl">
          <div className="flex items-center justify-between">
            <Button 
              variant="ghost" 
              onClick={() => window.history.back()}
              data-testid="button-back"
            >
              <ArrowLeft className="mr-2 h-4 w-4" />
              Volver
            </Button>
            <div className="flex items-center gap-3">
              <span className="text-sm text-muted-foreground">
                {user.firstName || user.email}
              </span>
              <Button variant="outline" asChild size="sm">
                <a href="/api/logout">Cerrar SesiÃ³n</a>
              </Button>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8 max-w-7xl">
        {/* Project Header */}
        <Card className="mb-8">
          <CardHeader>
            <div className="flex flex-col md:flex-row md:items-start md:justify-between gap-4">
              <div className="flex-1">
                <div className="flex items-center gap-3 mb-3">
                  <h1 className="text-3xl font-bold" data-testid="text-project-title">
                    {project.name}
                  </h1>
                  <StatusBadge status={project.status} />
                </div>
                <p className="text-muted-foreground text-lg mb-4" data-testid="text-project-description">
                  {project.description}
                </p>

                {/* Author */}
                {projectOwner && (
                  <div className="mb-4">
                    <button
                      onClick={() => setLocation(`/user/${projectOwner.id}`)}
                      className="flex items-center gap-2 text-sm text-muted-foreground hover:text-primary transition-colors"
                      data-testid="button-view-author-profile"
                    >
                      <Avatar className="h-6 w-6">
                        <AvatarImage src={projectOwner.profileImageUrl || undefined} />
                        <AvatarFallback className="text-xs">
                          {projectOwner.username?.[0]?.toUpperCase() || projectOwner.email?.[0]?.toUpperCase() || "?"}
                        </AvatarFallback>
                      </Avatar>
                      Publicado por:{" "}
                      <span className="font-semibold text-foreground hover:underline">
                        {projectOwner.username || projectOwner.email}
                      </span>
                    </button>
                  </div>
                )}

                <div className="flex flex-wrap gap-4 text-sm">
                  <div className="flex items-center gap-2">
                    <Package className="h-4 w-4 text-muted-foreground" />
                    <span className="font-medium">Material:</span>
                    <span className="inline-flex items-center gap-1.5 px-3 py-1 bg-primary/10 text-primary rounded-full font-medium">
                      {project.material}
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <FileText className="h-4 w-4 text-muted-foreground" />
                    <span className="font-medium">Archivo:</span>
                    <span className="text-muted-foreground" data-testid="text-stl-filename">
                      {project.stlFileName}
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <Calendar className="h-4 w-4 text-muted-foreground" />
                    <span className="font-medium">Publicado:</span>
                    <span className="text-muted-foreground">
                      {formatDistanceToNow(new Date(project.createdAt), { addSuffix: true, locale: es })}
                    </span>
                  </div>
                </div>
              </div>
              <div className="flex flex-col gap-2">
                <Button 
                  onClick={handleDownloadSTL}
                  data-testid="button-download-stl"
                >
                  <Download className="mr-2 h-4 w-4" />
                  Descargar STL
                </Button>
                {projectOwner && (
                  <Button 
                    variant="outline"
                    onClick={() => setChatDialogOpen(true)}
                    data-testid="button-contact-client"
                  >
                    <MessageCircle className="mr-2 h-4 w-4" />
                    Contactar Cliente
                  </Button>
                )}
              </div>
            </div>
          </CardHeader>
        </Card>

        {/* My Bid Info */}
        {myBid && (
          <Card className="mb-8 border-green-200 dark:border-green-900 bg-green-50 dark:bg-green-900/20">
            <CardContent className="pt-6">
              <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
                <div>
                  <h3 className="font-semibold text-green-900 dark:text-green-100">Tu Oferta</h3>
                  <p className="text-sm text-green-800 dark:text-green-200 mt-1">
                    Precio: ${myBid.price} â€¢ Entrega: {myBid.deliveryDays} dÃ­as
                  </p>
                  {myBid.status === "accepted" && (
                    <p className="text-sm text-green-700 dark:text-green-300 font-semibold mt-2">
                      âœ“ Tu oferta ha sido aceptada
                    </p>
                  )}
                </div>
                <div className="flex flex-col gap-2">
                  <span className={`px-3 py-1 rounded-full text-sm font-medium text-center ${
                    myBid.status === 'accepted' 
                      ? 'bg-green-200 text-green-800 dark:bg-green-800 dark:text-green-100'
                      : myBid.status === 'rejected'
                      ? 'bg-red-200 text-red-800 dark:bg-red-800 dark:text-red-100'
                      : 'bg-yellow-200 text-yellow-800 dark:bg-yellow-800 dark:text-yellow-100'
                  }`}>
                    {myBid.status === 'accepted' ? 'Aceptada' : myBid.status === 'rejected' ? 'Rechazada' : 'Pendiente'}
                  </span>
                  {myBid.status === 'pending' && (
                    <Button 
                      size="sm"
                      variant="outline"
                      onClick={() => setEditBidDialogOpen(true)}
                      data-testid="button-edit-bid"
                    >
                      <Edit2 className="mr-2 h-4 w-4" />
                      Editar Oferta
                    </Button>
                  )}
                  {myBid.status === 'rejected' && (
                    <Button 
                      size="sm"
                      onClick={() => setRebidDialogOpen(true)}
                      data-testid="button-rebid-rejected"
                    >
                      Volver a Ofertar
                    </Button>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </main>

      {projectOwner && user && (
        <ChatDialog
          open={chatDialogOpen}
          onOpenChange={setChatDialogOpen}
          otherUser={projectOwner}
          currentUserId={user.id}
          projectId={projectId!}
        />
      )}

      {myBid && projectId && (
        <BidEditDialog
          open={editBidDialogOpen}
          onOpenChange={setEditBidDialogOpen}
          bidId={myBid.id}
          projectId={projectId}
          currentPrice={myBid.price}
          currentDeliveryDays={myBid.deliveryDays}
          currentMessage={myBid.message}
        />
      )}

      {projectId && (
        <BidSubmissionDialog
          open={rebidDialogOpen}
          onOpenChange={setRebidDialogOpen}
          projectId={projectId}
        />
      )}
    </div>
  );
}

