import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { ProjectCard } from "@/components/ProjectCard";
import { EmptyState } from "@/components/EmptyState";
import { ProjectCardSkeleton } from "@/components/LoadingSkeleton";
import { ArrowLeft, Search, TrendingUp, Sliders } from "lucide-react";
import { useLocation } from "wouter";
import type { Project, MakerProfile } from "@shared/schema";

export default function ExploreProjects() {
  const { toast } = useToast();
  const { user, isLoading: authLoading } = useAuth();
  const [, setLocation] = useLocation();

  // Save the current explore page URL when this component mounts
  useEffect(() => {
    localStorage.setItem('previousProjectPath', '/maker/explore');
  }, []);
  const [searchQuery, setSearchQuery] = useState("");
  const [printerTypeFilter, setPrinterTypeFilter] = useState<string>("all");
  const [multicolorFilter, setMulticolorFilter] = useState<string>("all");
  const [minDimension, setMinDimension] = useState<number>(0);

  const { data: profile, isLoading: profileLoading } = useQuery<MakerProfile>({
    queryKey: ["/api/maker-profile"],
    enabled: !!user,
  });

  const { data: availableProjects, isLoading: availableLoading } = useQuery<(Project & { bidCount: number })[]>({
    queryKey: ["/api/projects/available"],
    enabled: !!user && !!profile,
  });

  const { data: myBidProjects, isLoading: myBidsLoading } = useQuery<(Project & { bidCount: number })[]>({
    queryKey: ["/api/projects/my-bids"],
    enabled: !!user && !!profile,
  });

  const { data: myBids } = useQuery<{ projectId: string; status: string; deliveryConfirmedAt?: string | null }[]>({
    queryKey: ["/api/bids/my-bids"],
    enabled: !!user && !!profile,
  });

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando...</p>
        </div>
      </div>
    );
  }

  if (!user) return null;

  const projectsLoading = availableLoading || myBidsLoading;
  const allProjects = [...(availableProjects || []), ...(myBidProjects?.filter(p => p.status !== "active") || [])];

  // Get project IDs that have been delivered
  const deliveredProjectIds = new Set(
    myBids
      ?.filter(bid => bid.deliveryConfirmedAt)
      .map(bid => bid.projectId) || []
  );

  const filteredProjects = allProjects.filter(project => {
    // Exclude completed/delivered projects
    if (project.status === "completed" || deliveredProjectIds.has(project.id)) {
      return false;
    }

    const matchesSearch = project.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      project.description.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesPrinter = printerTypeFilter === "all" || 
      (profile?.printerType === printerTypeFilter);

    const matchesMulticolor = multicolorFilter === "all" || 
      (multicolorFilter === "yes" && profile?.hasMulticolor) ||
      (multicolorFilter === "no" && !profile?.hasMulticolor);

    // Filter by dimensions - check if maker's max dimensions are >= minimum dimension
    let matchesDimensions = true;
    if (minDimension > 0 && profile) {
      const makerMaxX = profile.maxPrintDimensionX || 0;
      const makerMaxY = profile.maxPrintDimensionY || 0;
      const makerMaxZ = profile.maxPrintDimensionZ || 0;
      const minMax = Math.min(makerMaxX, makerMaxY, makerMaxZ);
      matchesDimensions = minMax >= minDimension;
    }

    return matchesSearch && matchesPrinter && matchesMulticolor && matchesDimensions;
  });

  const hasActiveFilters = printerTypeFilter !== "all" || multicolorFilter !== "all" || minDimension > 0;

  return (
    <div className="min-h-screen bg-background">
      {/* Sticky Header */}
      <header className="sticky top-0 z-50 bg-background/95 backdrop-blur-md border-b">
        <div className="container mx-auto px-4 py-3 max-w-7xl">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => window.history.back()}
            className="flex items-center gap-2"
            data-testid="button-back-to-maker"
          >
            <ArrowLeft className="h-4 w-4" />
            Volver
          </Button>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8 max-w-7xl">
        {/* Hero Section */}
        <div className="mb-12">
          <h1 className="text-4xl md:text-5xl font-bold tracking-tight mb-3 flex items-center gap-3">
            <TrendingUp className="h-10 w-10 text-primary" />
            Explora Proyectos
          </h1>
          <p className="text-lg text-muted-foreground">
            Descubre proyectos disponibles y coloca tus ofertas
          </p>
        </div>

        {/* Search & Filters */}
        <div className="mb-8 space-y-4">
          <div className="flex gap-3 items-center">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-muted-foreground" />
              <Input
                placeholder="Busca proyectos por nombre o descripciÃ³n..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 h-11"
                data-testid="input-search-projects"
              />
            </div>

            {/* Filters Popover */}
            <Popover>
              <PopoverTrigger asChild>
                <Button 
                  variant="outline" 
                  size="lg" 
                  className="flex items-center gap-2 whitespace-nowrap"
                  data-testid="button-open-filters"
                >
                  <Sliders className="h-4 w-4" />
                  Filtros
                  {hasActiveFilters && (
                    <span className="ml-2 inline-flex items-center justify-center h-5 w-5 rounded-full bg-primary text-primary-foreground text-xs font-bold">
                      {[printerTypeFilter !== "all", multicolorFilter !== "all", minDimension > 0].filter(Boolean).length}
                    </span>
                  )}
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-80 p-4" align="end" data-testid="popover-filters">
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-semibold mb-2 block">Tipo de Impresora</label>
                    <Select value={printerTypeFilter} onValueChange={setPrinterTypeFilter}>
                      <SelectTrigger data-testid="select-printer-type">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">Todas las impresoras</SelectItem>
                        <SelectItem value="Ender3">Ender 3</SelectItem>
                        <SelectItem value="BambooLab">Bambu Lab</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="text-sm font-semibold mb-2 block">Color</label>
                    <Select value={multicolorFilter} onValueChange={setMulticolorFilter}>
                      <SelectTrigger data-testid="select-multicolor">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">Cualquiera</SelectItem>
                        <SelectItem value="yes">Multicolor</SelectItem>
                        <SelectItem value="no">Monocolor</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="text-sm font-semibold mb-3 block">DimensiÃ³n MÃ­nima (mm)</label>
                    <div className="flex items-center gap-3">
                      <input
                        type="range"
                        min="0"
                        max="300"
                        step="10"
                        value={minDimension}
                        onChange={(e) => setMinDimension(Number(e.target.value))}
                        className="flex-1 h-2 bg-secondary rounded-lg appearance-none cursor-pointer"
                        data-testid="slider-min-dimension"
                      />
                      <span className="text-sm font-semibold bg-muted px-3 py-1 rounded-md w-16 text-center">
                        {minDimension}
                      </span>
                    </div>
                    <p className="text-xs text-muted-foreground mt-2">
                      Solo mostrar proyectos donde tu impresora pueda imprimir al menos {minDimension}mm en todos los lados
                    </p>
                  </div>

                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setPrinterTypeFilter("all");
                      setMulticolorFilter("all");
                      setMinDimension(0);
                    }}
                    className="w-full"
                    data-testid="button-reset-filters"
                  >
                    Limpiar Filtros
                  </Button>
                </div>
              </PopoverContent>
            </Popover>
          </div>
        </div>

        {/* Projects Grid */}
        <div>
          <div className="mb-8">
            <h2 className="text-3xl font-bold flex items-center gap-2">
              <Search className="h-8 w-8 text-primary" />
              Resultados
            </h2>
            <p className="text-muted-foreground mt-2">
              {filteredProjects?.length || 0} proyecto{(filteredProjects?.length || 0) !== 1 ? "s" : ""} disponible{(filteredProjects?.length || 0) !== 1 ? "s" : ""}
            </p>
          </div>

          {projectsLoading ? (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              <ProjectCardSkeleton />
              <ProjectCardSkeleton />
              <ProjectCardSkeleton />
            </div>
          ) : (filteredProjects?.length || 0) === 0 ? (
            <EmptyState
              icon={Search}
              title="Sin resultados"
              description={
                searchQuery || printerTypeFilter !== "all" || multicolorFilter !== "all" || minDimension > 0
                  ? "Intenta ajustar tus filtros de bÃºsqueda"
                  : "No hay proyectos disponibles en este momento"
              }
            />
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredProjects?.map((project) => {
                const hasMyBid = myBidProjects?.some(p => p.id === project.id);
                return (
                  <ProjectCard
                    key={project.id}
                    project={project}
                    onClick={() => {
                      // Save the current page before navigating
                      localStorage.setItem('previousProjectPath', '/maker/explore');
                      if (hasMyBid) {
                        setLocation(`/maker/project/${project.id}`);
                      } else {
                        setLocation(`/project/${project.id}`);
                      }
                    }}
                  />
                );
              })}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

client/src/pages/maker-reviews.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useRoute, useLocation } from "wouter";
import { ExternalLink } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { EmptyState } from "@/components/EmptyState";
import { ArrowLeft, Star, MessageCircle } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { es } from "date-fns/locale";
import type { Review, User } from "@shared/schema";

interface ReviewWithAuthor extends Review {
  fromUser?: User;
}

export default function MakerReviews() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const [userMatch, userParams] = useRoute("/user/:userId/reviews");
  const targetUserId = userParams?.userId;
  const isOwnReviews = !targetUserId; // If no userId param, showing own reviews

  const { data: reviews = [], isLoading } = useQuery<ReviewWithAuthor[]>({
    queryKey: isOwnReviews ? ["/api/reviews/my-reviews"] : ["/api/makers", targetUserId, "reviews"],
    queryFn: async () => {
      if (isOwnReviews) {
        const res = await fetch("/api/reviews/my-reviews", { credentials: "include" });
        if (!res.ok) throw new Error("Failed to fetch reviews");
        return res.json();
      } else {
        const res = await fetch(`/api/makers/${targetUserId}/reviews`, { credentials: "include" });
        if (!res.ok) throw new Error("Failed to fetch reviews");
        return res.json();
      }
    },
    enabled: !!user && (isOwnReviews || !!targetUserId),
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando reseÃ±as...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <main className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Back Button */}
        <Button
          variant="ghost"
          onClick={() => window.history.back()}
          className="mb-6"
          data-testid="button-back"
        >
          <ArrowLeft className="h-4 w-4 mr-2" />
          Volver
        </Button>

        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2">{isOwnReviews ? "Mis ReseÃ±as" : "ReseÃ±as del Maker"}</h1>
          <p className="text-muted-foreground">
            {reviews.length} reseÃ±a{reviews.length !== 1 ? "s" : ""} recibida{reviews.length !== 1 ? "s" : ""}
          </p>
        </div>

        {/* Reviews List */}
        {reviews.length === 0 ? (
          <EmptyState
            icon={MessageCircle}
            title="Sin reseÃ±as"
            description={isOwnReviews ? "AÃºn no has recibido ninguna reseÃ±a. Cuando completes tus primeros proyectos, tus clientes podrÃ¡n valorarte." : "Este maker aÃºn no ha recibido ninguna reseÃ±a."}
          />
        ) : (
          <div className="space-y-4">
            {reviews.map((review) => (
              <Card 
                key={review.id} 
                className="overflow-hidden cursor-pointer hover-elevate"
                onClick={() => review.projectId && setLocation(`/project/${review.projectId}`)}
                data-testid={`button-view-project-${review.projectId}`}
              >
                <CardContent className="pt-6 pb-6">
                  <div className="space-y-4">
                    {/* Review Header */}
                    <div className="flex items-center justify-between">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          review.fromUser?.id && setLocation(`/user/${review.fromUser.id}`);
                        }}
                        className="flex items-center gap-3 hover-elevate cursor-pointer transition-colors rounded-md p-1"
                        data-testid="button-view-user-profile"
                      >
                        <Avatar className="h-10 w-10">
                          <AvatarImage src={review.fromUser?.profileImageUrl || ""} alt={review.fromUser?.username || review.fromUser?.email || "Usuario"} />
                          <AvatarFallback>
                            {(review.fromUser?.username || review.fromUser?.email || "U").charAt(0)}
                          </AvatarFallback>
                        </Avatar>
                        <div className="text-left">
                          <p className="font-semibold hover:text-primary">{review.fromUser?.username || review.fromUser?.email || "Usuario AnÃ³nimo"}</p>
                          <p className="text-xs text-muted-foreground">
                            {formatDistanceToNow(new Date(review.createdAt), { locale: es, addSuffix: true })}
                          </p>
                        </div>
                      </button>

                      {/* Rating Stars & Project Link */}
                      <div className="flex items-center gap-3">
                        <div className="flex items-center gap-2">
                          <div className="flex items-center gap-1">
                            {[...Array(5)].map((_, i) => {
                              const rating = parseFloat(String(review.rating || 0));
                              return (
                                <Star
                                  key={i}
                                  className={`h-4 w-4 ${
                                    i < Math.floor(rating)
                                      ? "fill-yellow-400 text-yellow-400"
                                      : i < rating
                                      ? "fill-yellow-400 text-yellow-400 opacity-50"
                                      : "text-muted-foreground"
                                  }`}
                                />
                              );
                            })}
                          </div>
                          <Badge variant="outline">
                            {parseFloat(String(review.rating || 0)).toFixed(1)}
                          </Badge>
                        </div>
                        {review.projectId && (
                          <ExternalLink className="h-4 w-4 text-muted-foreground" />
                        )}
                      </div>
                    </div>

                    {/* Review Comment */}
                    {review.comment && (
                      <div className="bg-muted/50 rounded-lg p-4">
                        <p className="text-sm text-foreground">{review.comment}</p>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

client/src/pages/chats-split.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ChatListItem } from "@/components/ChatListItem";
import { ChatWindow } from "@/components/ChatWindow";
import { ArrowLeft, MessageCircle } from "lucide-react";
import type { User } from "@shared/schema";

interface Conversation {
  userId: string;
  projectId?: string;
  marketplaceDesignId?: string;
  lastMessage?: { content: string; createdAt: string };
  unreadCount: number;
  user?: User;
}

interface SelectedConversationKey {
  userId: string;
  projectId?: string;
  marketplaceDesignId?: string;
}

export default function ChatsSplitPage() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const [selectedConvKey, setSelectedConvKey] = useState<SelectedConversationKey | null>(null);

  // Fetch conversations
  const { data: allConversations = [], isLoading } = useQuery({
    queryKey: ["/api/my-conversations-full"],
    queryFn: async () => {
      const res = await fetch("/api/my-conversations-full", {
        credentials: "include",
      });
      if (!res.ok) throw new Error("Failed to fetch conversations");
      return res.json();
    },
    enabled: !!user,
    refetchInterval: 3000,
  });

  // Fetch other user details
  const { data: otherUser } = useQuery<User | null>({
    queryKey: ["/api/user", selectedConvKey?.userId],
    queryFn: async () => {
      const res = await fetch(`/api/user/${selectedConvKey?.userId}`, {
        credentials: "include",
      });
      if (!res.ok) return null;
      return res.json();
    },
    enabled: !!selectedConvKey?.userId,
  });

  const filteredConversations = allConversations;

  // Get selected conversation details - match by userId + context
  const selectedConv = selectedConvKey ? allConversations.find((c: Conversation) => {
    return (
      c.userId === selectedConvKey.userId &&
      c.projectId === selectedConvKey.projectId &&
      c.marketplaceDesignId === selectedConvKey.marketplaceDesignId
    );
  }) : undefined;

  // If no conversation selected, select the first one
  useEffect(() => {
    if (!selectedConvKey && filteredConversations.length > 0) {
      const first = filteredConversations[0];
      setSelectedConvKey({
        userId: first.userId,
        projectId: first.projectId,
        marketplaceDesignId: first.marketplaceDesignId,
      });
    }
  }, [filteredConversations, selectedConvKey]);

  return (
    <div className="h-screen flex flex-col bg-background">
      {/* Header */}
      <header className="border-b px-4 py-4 sticky top-0 z-50 bg-background/95 backdrop-blur flex items-center justify-between">
        <div className="flex items-center gap-3">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => window.history.back()}
            data-testid="button-back"
          >
            <ArrowLeft className="h-4 w-4" />
          </Button>
          <div>
            <h1 className="text-xl font-bold">Mensajes</h1>
            {user && (
              <p className="text-xs text-muted-foreground">
                {user.firstName || user.email}
              </p>
            )}
          </div>
        </div>
        <Button variant="outline" asChild size="sm">
          <a href="/api/logout">Cerrar SesiÃ³n</a>
        </Button>
      </header>

      <div className="flex flex-1 min-h-0">
        {/* Conversations List */}
        <div className="w-80 border-r flex flex-col bg-card/50">
          {/* Conversations */}
          <div className="flex-1 overflow-y-auto">
            {isLoading ? (
              <div className="p-4 text-center text-muted-foreground">
                Cargando conversaciones...
              </div>
            ) : filteredConversations.length === 0 ? (
              <div className="p-8 text-center">
                <MessageCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4 opacity-50" />
                <p className="text-muted-foreground text-sm">
                  Sin conversaciones aÃºn
                </p>
              </div>
            ) : (
              filteredConversations.map((conv: Conversation) => {
                const uniqueKey = `${conv.userId}-${conv.projectId || ''}-${conv.marketplaceDesignId || ''}`;
                const isProjectDeleted = conv.projectId && conv.project?.deletedAt;
                const isDesignDeleted = conv.marketplaceDesignId && conv.design?.deletedAt;
                return (
                <ChatListItem
                  key={uniqueKey}
                  userId={conv.userId}
                  userName={(conv.user?.username || conv.user?.email) || undefined}
                  userImage={conv.user?.profileImageUrl || undefined}
                  projectName={conv.project?.name}
                  designName={conv.design?.title}
                  projectImage={conv.project?.stlImageUrl}
                  designImage={conv.design?.imageUrl}
                  projectId={conv.projectId}
                  designId={conv.marketplaceDesignId}
                  projectStatus={conv.project?.status}
                  lastMessage={conv.lastMessage?.content || undefined}
                  lastMessageTime={conv.lastMessage?.createdAt || undefined}
                  unreadCount={conv.unreadCount || 0}
                  isActive={
                    selectedConvKey?.userId === conv.userId &&
                    selectedConvKey?.projectId === conv.projectId &&
                    selectedConvKey?.marketplaceDesignId === conv.marketplaceDesignId
                  }
                  isProjectDeleted={isProjectDeleted}
                  isDesignDeleted={isDesignDeleted}
                  onClick={() =>
                    setSelectedConvKey({
                      userId: conv.userId,
                      projectId: conv.projectId,
                      marketplaceDesignId: conv.marketplaceDesignId,
                    })
                  }
                />
                );
              })
            )}
          </div>
        </div>

        {/* Chat Window */}
        <div className="flex-1 flex flex-col">
          {selectedConvKey && selectedConv && otherUser ? (
            <ChatWindow
              otherUserId={selectedConvKey.userId}
              otherUser={otherUser}
              currentUserId={user?.id || ""}
              projectId={selectedConv.projectId}
              marketplaceDesignId={selectedConv.marketplaceDesignId}
              project={selectedConv.project}
              design={selectedConv.design}
            />
          ) : (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <MessageCircle className="h-16 w-16 text-muted-foreground mx-auto mb-4 opacity-30" />
                <p className="text-muted-foreground">
                  Selecciona una conversaciÃ³n para comenzar
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/maker-profile.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { MakerProfileDialog } from "@/components/MakerProfileDialog";
import { ArrowLeft, Edit2, Star, Printer, DollarSign, Wallet } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { MakerProfile } from "@shared/schema";

export default function MakerProfile() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const [profileDialogOpen, setProfileDialogOpen] = useState(false);
  const [payoutMethod, setPayoutMethod] = useState("");
  const [stripeEmail, setStripeEmail] = useState("");
  const [paypalEmail, setPaypalEmail] = useState("");
  const [bankIban, setBankIban] = useState("");
  const [bankName, setBankName] = useState("");
  const [payoutAmount, setPayoutAmount] = useState("");
  const { toast } = useToast();

  const { data: profile, isLoading: profileLoading } = useQuery<MakerProfile>({
    queryKey: ["/api/maker-profile"],
    enabled: !!user,
  });

  const { data: balance } = useQuery<any>({
    queryKey: ["/api/maker/balance"],
    enabled: !!user,
  });

  const { data: payouts } = useQuery<any>({
    queryKey: ["/api/maker/payouts"],
    enabled: !!user,
  });

  const updatePayoutMethodMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/maker/payout-method", data);
    },
    onSuccess: () => {
      toast({ title: "MÃ©todo de pago actualizado", description: "Tu configuraciÃ³n ha sido guardada." });
      queryClient.invalidateQueries({ queryKey: ["/api/maker-profile"] });
      setPayoutMethod("");
      setStripeEmail("");
      setPaypalEmail("");
      setBankIban("");
      setBankName("");
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    },
  });

  const requestPayoutMutation = useMutation({
    mutationFn: async (amount: string) => {
      return apiRequest("POST", "/api/maker/request-payout", { amount });
    },
    onSuccess: () => {
      toast({ title: "Payout solicitado", description: "Tu solicitud de pago ha sido procesada." });
      queryClient.invalidateQueries({ queryKey: ["/api/maker/payouts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/maker/balance"] });
      setPayoutAmount("");
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    },
  });

  const handleUpdatePayoutMethod = () => {
    if (!payoutMethod) {
      toast({ title: "Error", description: "Selecciona un mÃ©todo de pago", variant: "destructive" });
      return;
    }

    const data: any = { method: payoutMethod };

    if (payoutMethod === "stripe" && stripeEmail) {
      data.stripeEmail = stripeEmail;
    } else if (payoutMethod === "paypal" && paypalEmail) {
      data.paypalEmail = paypalEmail;
    } else if (payoutMethod === "bank" && bankIban && bankName) {
      data.bankAccountIban = bankIban;
      data.bankAccountName = bankName;
    }

    updatePayoutMethodMutation.mutate(data);
  };

  const handleRequestPayout = () => {
    if (!payoutAmount || parseFloat(payoutAmount) <= 0) {
      toast({ title: "Error", description: "Ingresa una cantidad vÃ¡lida", variant: "destructive" });
      return;
    }
    requestPayoutMutation.mutate(payoutAmount);
  };

  if (profileLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando perfil...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      <main className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Back Button */}
        <Button
          variant="ghost"
          onClick={() => window.history.back()}
          className="mb-6"
          data-testid="button-back"
        >
          <ArrowLeft className="h-4 w-4 mr-2" />
          Volver
        </Button>

        {/* Profile Header Card */}
        <Card className="mb-8 border-2 border-primary/10 bg-gradient-to-r from-primary/5 via-transparent to-secondary/5 dark:from-primary/10 dark:via-transparent dark:to-secondary/10">
          <CardContent className="pt-8 pb-8">
            <div className="flex flex-col md:flex-row items-start md:items-center gap-8">
              {/* Avatar */}
              <Avatar className="h-24 w-24 flex-shrink-0">
                <AvatarImage src={user.profileImageUrl || ""} alt={user.firstName || "User"} />
                <AvatarFallback className="text-2xl">
                  {(user.firstName || "U").charAt(0)}
                </AvatarFallback>
              </Avatar>

              {/* User Info */}
              <div className="flex-1 space-y-4">
                <div>
                  <h1 className="text-4xl font-bold mb-1">{user.username}</h1>
                  {user.showFullName && (
                    <p className="text-muted-foreground">{user.firstName} {user.lastName}</p>
                  )}
                </div>

                {/* Rating */}
                {profile && (
                  <div className="flex items-center gap-4 flex-wrap">
                    <div className="flex items-center gap-2">
                      <div className="flex items-center gap-1">
                        {[...Array(5)].map((_, i) => {
                          const rating = typeof profile.rating === 'string' ? parseFloat(profile.rating) : (profile.rating || 0);
                          return (
                            <Star
                              key={i}
                              className={`h-5 w-5 ${
                                i < Math.floor(rating)
                                  ? "fill-yellow-400 text-yellow-400"
                                  : i < rating
                                  ? "fill-yellow-400 text-yellow-400 opacity-50"
                                  : "text-muted-foreground"
                              }`}
                            />
                          );
                        })}
                      </div>
                      <span className="font-semibold text-lg">
